using System.Collections; using System.Collections.Generic; using UnityEngine;  public class bgscale : MonoBehaviour {  	// Use this for initialization     //void Start () {      //    SpriteRenderer sr = GetComponent<SpriteRenderer>();     //    Vector3 tempScale = transform.localScale;      //    float height = sr.bounds.size.y;     //    float width = sr.bounds.size.x;       //    float worldheight = Camera.main.orthographicSize * 5f;     //    float worldwidth = worldheight * Screen.width / Screen.height;           //    tempScale.y = worldheight / height;     //    tempScale.x = worldwidth / width;      //    transform.localScale = tempScale;     //}     void Awake()     {         SpriteRenderer spriteRenderer = GetComponent<SpriteRenderer>();          float cameraHeight = Camera.main.orthographicSize * 0.8f;         Vector2 cameraSize = new Vector2(Camera.main.aspect * cameraHeight, cameraHeight);         Vector2 spriteSize = spriteRenderer.sprite.bounds.size;          Vector2 scale = transform.localScale;         if (cameraSize.x >= cameraSize.y)         { // Landscape (or equal)             scale *= cameraSize.x / spriteSize.x;         }         else         { // Portrait             scale *= cameraSize.y / spriteSize.y;         }          transform.position = Vector2.zero; // Optional         transform.localScale = scale;     } 	  	// Update is called once per frame 	void Update () { 		 	} } 